import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../styles/Services.css";

const Services = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Sliding images related to services
  const serviceSlides = [
    {
      id: 1,
      title: "Cloud Architecture & DevOps",
      description: "Design and implement scalable cloud-native solutions using AWS, GCP, and Kubernetes.",
      image: "https://images.unsplash.com/photo-1550751827-4bd374c3f58b?auto=format&fit=crop&w=1200&q=80",
      technologies: ["AWS", "GCP", "Kubernetes", "Terraform", "Docker", "CI/CD"]
    },
    {
      id: 2,
      title: "Full-Stack Development",
      description: "End-to-end development of secure, scalable applications using modern technologies.",
      image: "https://images.unsplash.com/photo-1555066931-4365d14bab8c?auto=format&fit=crop&w=1200&q=80",
      technologies: ["Elixir/Phoenix", "React/TypeScript", "Python", "Node.js", "PostgreSQL", "Redis"]
    },
    {
      id: 3,
      title: "Microservices & API Development",
      description: "Architect and develop distributed systems with microservices architecture.",
      image: "https://images.unsplash.com/photo-1550751827-4bd374c3f58b?auto=format&fit=crop&w=1200&q=80",
      technologies: ["Microservices", "RESTful APIs", "GraphQL", "gRPC", "Message Queues", "API Gateways"]
    },
    {
      id: 4,
      title: "Fintech & Security Solutions",
      description: "Specialized in financial technology platforms with banking-grade security.",
      image: "https://images.unsplash.com/photo-1563014959-7aaa83350985?auto=format&fit=crop&w=1200&q=80",
      technologies: ["SOC 2 Compliance", "PCI DSS", "Authentication", "Encryption", "Security Audits"]
    },
    {
      id: 5,
      title: "Performance Optimization",
      description: "Optimize existing systems for better performance, scalability, and reliability.",
      image: "https://images.unsplash.com/photo-1553877522-43269d4ea984?auto=format&fit=crop&w=1200&q=80",
      technologies: ["Redis Caching", "Database Optimization", "Monitoring", "Load Testing", "Profiling"]
    }
  ];

  // Auto-rotate slides every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % serviceSlides.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [serviceSlides.length]);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % serviceSlides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + serviceSlides.length) % serviceSlides.length);
  };

  const services = [
    {
      title: "Cloud Architecture & DevOps",
      description: "Design and implement scalable cloud-native solutions using AWS, GCP, and Kubernetes. Expert in infrastructure as code with Terraform, CI/CD pipelines, and GitOps methodologies.",
      icon: "fas fa-cloud",
      technologies: ["AWS", "GCP", "Kubernetes", "Terraform", "Docker", "CI/CD"],
      experience: "8+ years of experience building SOC 2-compliant systems handling 5,000+ requests per minute"
    },
    {
      title: "Full-Stack Development",
