import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useTheme } from "../contexts/ThemeContext";
import "../styles/Navbar.css";

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { theme, toggleTheme } = useTheme();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  // Determine classes
  const navbarClasses = ["navbar"];
  if (isScrolled) {
    navbarClasses.push("scrolled");
  }

  const menuClasses = ["nav-menu"];
  if (isMenuOpen) {
    menuClasses.push("active");
  }

  const toggleClasses = ["nav-toggle"];
  if (isMenuOpen) {
    toggleClasses.push("active");
  }

  // Determine theme label
  const themeLabel = theme === "light" ? "Switch to dark mode" : "Switch to light mode";

  return (
    <nav className={navbarClasses.join(" ")}>
      <div className="nav-container">
        <Link to="/" className="nav-logo">
          HM
        </Link>

        <div className={menuClasses.join(" ")}>
          <Link to="/" className="nav-link" onClick={() => setIsMenuOpen(false)}>
            Home
          </Link>
          <Link to="/projects" className="nav-link" onClick={() => setIsMenuOpen(false)}>
            Projects
          </Link>
          <Link to="/contact" className="nav-link" onClick={() => setIsMenuOpen(false)}>
            Contact
          </Link>
        </div>

        <div className="nav-actions">
          <button 
            className="theme-toggle" 
            onClick={toggleTheme}
            aria-label={themeLabel}
          >
            {theme === "light" ? (
              <i className="fas fa-moon"></i>
            ) : (
              <i className="fas fa-sun"></i>
            )}
          </button>
        </div>

        <div className={toggleClasses.join(" ")} onClick={toggleMenu}>
          <span className="bar"></span>
          <span className="bar"></span>
          <span className="bar"></span>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
